 app.service('fetch', function($q,$cookies) {
    this.set_login = function(data){
        $cookies.remove('acs');
        $cookies.putObject('acs', data);  
    }
    this.get_login = function(data){
        var cookie = $cookies.get('acs');
        if(cookie){
            var obj = JSON.parse(cookie);
        }
        return obj
    } 
    this.remove_login = function(){
        $cookies.remove('acs');      
    } 
    this.set_room = function(data){
        $cookies.remove('room');
        $cookies.putObject('room', data);  
    }
    this.get_room = function(data){
        var cookie = $cookies.get('room');
        if(cookie){
            var obj = JSON.parse(cookie);
        }
        return obj
    } 
    this.remove_room = function(){
        $cookies.remove('room');      
    }     
});